import React, { useState } from "react";
import apiClient from "../../api";

const VerifyAadhaar = () => {
  const [aadhaar, setAadhaar] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [message, setMessage] = useState("");

  const sendOtp = async () => {
    if (aadhaar.length !== 12) return setMessage("Enter valid 12-digit Aadhaar");

    const res = await apiClient.post("/verify/aadhaar/send-otp", {
      aadhaarNumber: aadhaar,
      patientId: 1 // prototype only
    });

    setOtpSent(true);
    setMessage("OTP sent (Prototype): " + res.data.otp);
  };

  const verifyOtp = async () => {
    const res = await apiClient.post("/verify/aadhaar/verify", {
      otp,
      aadhaarNumber: aadhaar,
      patientId: 1
    });

    setMessage(res.data.message);
  };

  return (
    <div className="page-card">
      <h2>Aadhaar Verification</h2>

      <input
        placeholder="Enter Aadhaar"
        value={aadhaar}
        onChange={(e) => setAadhaar(e.target.value.replace(/\D/g, ""))}
      />

      {!otpSent && (
        <button onClick={sendOtp} className="primary-button glossy-btn">
          Send OTP
        </button>
      )}

      {otpSent && (
        <>
          <input
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={verifyOtp} className="primary-button glossy-btn">
            Verify OTP
          </button>
        </>
      )}

      <p>{message}</p>
    </div>
  );
};

export default VerifyAadhaar;
