import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Welcome.css';

const Welcome = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  // Handle scroll to add shadow to header
  React.useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.8, ease: 'easeOut' },
    },
  };

  const floatingVariants = {
    animate: {
      y: [0, -20, 0],
      transition: {
        duration: 3,
        repeat: Infinity,
        ease: 'easeInOut',
      },
    },
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#F8FFFC] via-white to-[#F0FFFE]">
      {/* HEADER */}
      <header
        className={`sticky top-0 z-50 transition-all duration-300 ${
          isScrolled
            ? 'backdrop-blur-lg bg-white bg-opacity-80 shadow-lg'
            : 'backdrop-blur-sm bg-white bg-opacity-40'
        }`}
      >
        <div className="max-w-7xl mx-auto px-6 md:px-8 py-4 flex items-center justify-between">
          {/* Logo */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6 }}
            className="flex items-center gap-2"
          >
            <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-[#00C896] to-[#007C6E] flex items-center justify-center shadow-lg">
              <span className="text-white font-bold text-lg">LT</span>
            </div>
            <h1 className="text-2xl font-bold text-[#333333]">LifeTag</h1>
          </motion.div>

          {/* Navigation Buttons */}
          <div className="flex items-center gap-4">
            <Link
              to="/login"
              className="px-6 py-2.5 text-[#00C896] font-semibold border-2 border-[#00C896] rounded-lg hover:bg-[#00C896] hover:text-white transition-all duration-300 hover:shadow-lg"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="px-6 py-2.5 bg-gradient-to-r from-[#00C896] to-[#007C6E] text-white font-semibold rounded-lg shadow-lg hover:shadow-2xl hover:scale-105 transition-all duration-300"
            >
              Sign Up
            </Link>
          </div>
        </div>
      </header>

      {/* HERO SECTION */}
      <main className="relative min-h-[calc(100vh-80px)] flex items-center justify-center px-6 md:px-8 py-12">
        {/* Background glow */}
        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-96 h-96 rounded-full bg-gradient-to-r from-[#00C896] to-[#C8FFF0] opacity-10 blur-3xl pointer-events-none"></div>

        <div className="relative max-w-7xl w-full grid md:grid-cols-2 gap-12 items-center">
          {/* LEFT SIDE - TEXT & BUTTONS */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            className="flex flex-col gap-8"
          >
            {/* Headline */}
            <motion.h2
              variants={itemVariants}
              className="text-5xl md:text-6xl lg:text-7xl font-bold text-[#333333] leading-tight"
            >
              Your Health,
              <br />
              <span className="bg-gradient-to-r from-[#00C896] to-[#007C6E] bg-clip-text text-transparent">
                Digitally Secured.
              </span>
            </motion.h2>

            {/* Subtext */}
            <motion.p
              variants={itemVariants}
              className="text-lg md:text-xl text-[#666666] max-w-lg leading-relaxed"
            >
              A single ID for all your medical records ‚Äî secure, smart, and always with you. Manage your health identity with confidence.
            </motion.p>

            {/* CTA Buttons */}
            <motion.div
              variants={itemVariants}
              className="flex flex-col sm:flex-row gap-4 pt-4"
            >
              {/* Login Button */}
              <Link
                to="/login"
                className="group relative px-8 py-4 bg-gradient-to-r from-[#00C896] to-[#007C6E] text-white font-bold rounded-xl text-center overflow-hidden transition-all duration-300 hover:shadow-2xl hover:scale-105"
              >
                <span className="relative z-10">Login</span>
                <div className="absolute inset-0 bg-white opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
              </Link>

              {/* Sign Up Button */}
              <Link
                to="/register"
                className="px-8 py-4 border-2 border-[#00C896] text-[#00C896] font-bold rounded-xl text-center transition-all duration-300 hover:bg-[#C8FFF0] hover:border-[#007C6E] hover:text-[#007C6E]"
              >
                Sign Up
              </Link>
            </motion.div>

            {/* Trust Badge */}
            <motion.div variants={itemVariants} className="flex items-center gap-2 pt-4">
              <div className="flex -space-x-2">
                {[1, 2, 3].map((i) => (
                  <div
                    key={i}
                    className="w-8 h-8 rounded-full bg-gradient-to-br from-[#00C896] to-[#007C6E] border-2 border-white flex items-center justify-center text-white text-xs font-bold"
                  >
                    {i}
                  </div>
                ))}
              </div>
              <span className="text-sm text-[#666666] font-medium">
                Trusted by 10K+ healthcare users
              </span>
            </motion.div>
          </motion.div>

          {/* RIGHT SIDE - PHONE MOCKUP & FLOATING ICONS */}
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="relative h-96 md:h-[500px] flex items-center justify-center"
          >
            {/* Glowing background circle */}
            <div className="absolute inset-0 rounded-full bg-gradient-to-r from-[#00C896] to-[#C8FFF0] opacity-20 blur-3xl"></div>

            {/* Phone Mockup */}
            <motion.div
              animate={{ y: [0, -10, 0] }}
              transition={{ duration: 4, repeat: Infinity, ease: 'easeInOut' }}
              className="relative z-10 w-64 h-80 md:w-80 md:h-96 bg-gradient-to-br from-[#00C896] to-[#007C6E] rounded-3xl shadow-2xl p-2"
            >
              {/* Phone Inner Screen */}
              <div className="w-full h-full bg-white rounded-2xl flex flex-col items-center justify-center p-6 relative overflow-hidden">
                {/* QR Code SVG */}
                <svg
                  className="w-40 h-40 mb-4"
                  viewBox="0 0 100 100"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <rect x="10" y="10" width="20" height="20" fill="#00C896" />
                  <rect x="70" y="10" width="20" height="20" fill="#00C896" />
                  <rect x="10" y="70" width="20" height="20" fill="#00C896" />
                  <rect x="40" y="40" width="20" height="20" fill="#00C896" />
                  <rect x="15" y="15" width="10" height="10" fill="white" />
                  <rect x="75" y="15" width="10" height="10" fill="white" />
                  <rect x="15" y="75" width="10" height="10" fill="white" />
                </svg>
                <p className="text-sm font-bold text-[#00C896]">Health ID</p>
                <p className="text-xs text-[#666666] mt-2">Scan to verify</p>
              </div>
            </motion.div>

            {/* Floating Icons */}
            <motion.div
              variants={floatingVariants}
              animate="animate"
              className="absolute top-10 left-0 w-16 h-16 bg-white rounded-2xl shadow-lg flex items-center justify-center"
            >
              <span className="text-3xl">‚ù§Ô∏è</span>
            </motion.div>

            <motion.div
              variants={floatingVariants}
              animate="animate"
              transition={{ delay: 0.5 }}
              className="absolute bottom-20 right-0 w-16 h-16 bg-white rounded-2xl shadow-lg flex items-center justify-center"
            >
              <span className="text-3xl">üõ°Ô∏è</span>
            </motion.div>

            <motion.div
              variants={floatingVariants}
              animate="animate"
              transition={{ delay: 1 }}
              className="absolute top-1/2 right-10 w-16 h-16 bg-white rounded-2xl shadow-lg flex items-center justify-center"
            >
              <span className="text-3xl">üß¨</span>
            </motion.div>
          </motion.div>
        </div>
      </main>

      {/* FEATURE STRIP */}
      <motion.section
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
        viewport={{ once: true }}
        className="relative py-20 px-6 md:px-8 bg-gradient-to-r from-[#C8FFF0] to-[#F8FFFC]"
      >
        <div className="max-w-7xl mx-auto grid md:grid-cols-3 gap-8">
          {/* Feature 1 */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0 }}
            viewport={{ once: true }}
            className="flex flex-col items-center text-center gap-4"
          >
            <div className="w-16 h-16 bg-gradient-to-br from-[#00C896] to-[#007C6E] rounded-2xl flex items-center justify-center text-3xl shadow-lg">
              üîí
            </div>
            <h3 className="text-xl font-bold text-[#333333]">Secure Cloud Health Records</h3>
            <p className="text-[#666666]">End-to-end encrypted storage with bank-level security.</p>
          </motion.div>

          {/* Feature 2 */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
            className="flex flex-col items-center text-center gap-4"
          >
            <div className="w-16 h-16 bg-gradient-to-br from-[#00C896] to-[#007C6E] rounded-2xl flex items-center justify-center text-3xl shadow-lg">
              üß†
            </div>
            <h3 className="text-xl font-bold text-[#333333]">AI-Powered Smart Insights</h3>
            <p className="text-[#666666]">Get personalized health recommendations instantly.</p>
          </motion.div>

          {/* Feature 3 */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            viewport={{ once: true }}
            className="flex flex-col items-center text-center gap-4"
          >
            <div className="w-16 h-16 bg-gradient-to-br from-[#00C896] to-[#007C6E] rounded-2xl flex items-center justify-center text-3xl shadow-lg">
              üåç
            </div>
            <h3 className="text-xl font-bold text-[#333333]">Access Anywhere, Anytime</h3>
            <p className="text-[#666666]">Your health ID is always accessible on any device.</p>
          </motion.div>
        </div>
      </motion.section>

      {/* FOOTER */}
      <footer className="bg-gradient-to-r from-[#00C896] to-[#007C6E] text-white py-12 px-6 md:px-8">
        <div className="max-w-7xl mx-auto">
          {/* Footer Links */}
          <div className="flex flex-col md:flex-row items-center justify-center gap-8 md:gap-12 mb-8">
            <a href="#" className="hover:text-[#C8FFF0] transition-colors font-medium">
              Terms of Service
            </a>
            <a href="#" className="hover:text-[#C8FFF0] transition-colors font-medium">
              Privacy Policy
            </a>
            <div className="flex items-center gap-2">
              <span className="font-medium">Language:</span>
              <button className="px-2 py-1 hover:bg-white hover:bg-opacity-20 rounded transition-colors">
                EN
              </button>
              <span className="text-white text-opacity-50">|</span>
              <button className="px-2 py-1 hover:bg-white hover:bg-opacity-20 rounded transition-colors">
                ‡§π‡§ø‡§Ç‡§¶‡•Ä
              </button>
              <span className="text-white text-opacity-50">|</span>
              <button className="px-2 py-1 hover:bg-white hover:bg-opacity-20 rounded transition-colors">
                ‡§Æ‡§∞‡§æ‡§†‡•Ä
              </button>
            </div>
          </div>

          {/* Copyright */}
          <div className="text-center text-white text-opacity-80 text-sm">
            <p>&copy; 2025 LifeTag. All rights reserved. | Secure Health Records, Simplified.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Welcome;
