import React, { useState } from "react";
import apiClient from "../../api";

const AdminBlockUser = () => {
  const [userId, setUserId] = useState("");
  const [role, setRole] = useState("patient");
  const [message, setMessage] = useState("");

  const block = async () => {
    const res = await apiClient.post("/admin/block", { userId, role });
    setMessage(res.data.message);
  };

  const unblock = async () => {
    const res = await apiClient.post("/admin/unblock", { userId, role });
    setMessage(res.data.message);
  };

  return (
    <div className="page-wrapper">
      <h2 className="page-header">Block / Unblock User</h2>

      <div className="glass-card" style={{ padding: "1.5rem" }}>
        <label>User ID</label>
        <input
          className="modern-input"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />

        <label>User Role</label>
        <select
          className="modern-input"
          value={role}
          onChange={(e) => setRole(e.target.value)}
        >
          <option value="patient">Patient</option>
          <option value="doctor">Doctor</option>
        </select>

        <button className="primary-button glossy-btn" onClick={block}>
          Block User
        </button>
        <button className="primary-button glossy-btn" onClick={unblock} style={{ marginTop: "1rem" }}>
          Unblock User
        </button>

        {message && <p style={{ marginTop: "1rem", color: "green" }}>{message}</p>}
      </div>
    </div>
  );
};

export default AdminBlockUser;
